// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IUsageFacet {
    function approveToService(bytes32 _serviceId, uint256 _amount) external;
    function getServiceAllowance(bytes32 _serviceId, address _holder) external view returns (uint256);

    // register services by integrating contract
    function registerService(
        address _serviceContract,
        bytes32 _serviceId,
        string calldata _serviceName
    ) external;

    function getTotalVotes(address _holder)
        external
        view
        returns (uint256 totalVotes_);

    function getAvailableVotes(address _holder)
        external
        view
        returns (uint256 availableVotes_);

    function getTotalUsingVotes(address _holder)
        external
        view
        returns (uint256 totalUsingVote_);

    function addVotesToService(
        bytes32 _serviceId,
        address _holder,
        uint256 _amount
    ) external;

    function removeVotesFromService(
        bytes32 _serviceId,
        address _holder,
        uint256 _amount
    ) external;

    function getVotesOnService(
        address _serviceContract,
        bytes32 _serviceId,
        address _holder
    ) external view returns (uint256);
}
